-- ----------------------------
-- Table structure for `news`
-- ----------------------------
DROP TABLE IF EXISTS `news`;
CREATE TABLE `news` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(25) NOT NULL DEFAULT '' COMMENT '标题',
  `desc` varchar(255) NOT NULL DEFAULT '' COMMENT '摘要',
  `content` text COMMENT '内容',
  `cover` varchar(255) NOT NULL DEFAULT '' COMMENT '封面图',
  `category` tinyint(2) unsigned NOT NULL DEFAULT '1' COMMENT '分类  1-行业 2-公司',
  `tags` varchar(255) NOT NULL DEFAULT '' COMMENT '标签',
  `admin_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '编辑ID',
  `status` tinyint(2) NOT NULL DEFAULT '2' COMMENT '1-发布 2-待发布 -9-删除',
  `ctime` int(11) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=22 DEFAULT CHARSET=utf8;

